    padding: 0.5rem; 
  }

  .header { 
    padding: 1rem; 
    margin-bottom: 0.75rem; 
  }

  .card { 
    padding: 0.75rem; 
    margin-bottom: 0.5rem; 
  }

  .responsive-table { 
    font-size: 0.8rem; 
  }

  .button { 
    padding: 0.4rem 0.8rem; 
    font-size: 0.8rem; 
  }

  .input { 
    padding: 0.5rem; 
    font-size: 0.9rem; 
  }

  .badge {
    font-size: 0.7rem;
    padding: 0.2rem 0.4rem;
  }
}

/* Touch device improvements */
@media (hover: none) and (pointer: coarse) {
  .button {
    min-height: 44px;
    min-width: 44px;
  }

  .responsive-table th,
  .responsive-table td {
    padding: 0.75rem 0.5rem;
  }
}

/* Print styles */
@media print {
  .container {
    max-width: none;
    margin: 0;
    padding: 0;
    background: white;
  }

  .header {
    background: #666 !important;
    color: black !important;
  }

  .button {
    display: none;
  }
}
