        }
        .card-item .card-top {
          display: flex;
          justify-content: space-between;
          align-items: center;
          font-size: 12px;
          font-weight: 600;
          color: #5e6c84;
          gap: 10px;
        }
        .card-item .card-top .card-id {
          font-family: monospace;
          color: #a2adba;
          user-select: text;
        }
        .card-item .card-tags .tag {
          border-radius: 6px;
          padding: 3px 7px;
          font-size: 11px;
          font-weight: 600;
          text-transform: uppercase;
          white-space: nowrap;
          user-select: none;
          margin-right: 6px;
        }
        .tag-task {
          background: #e1efff;
          color: #1976d2;
        }
        .tag-bug {
          background: #ffebec;
          color: #d62127;
        }
        .tag-subtask {
          background: #f5e6ff;
          color: #772da0;
        }
        .card-item .card-title {
          font-weight: 600;
          font-size: 14px;
          color: #2b3a59;
        }
        .card-item .card-meta {
          display: flex;
